tasks.command:
mkdir -p assets components && echo 'Generating index.html, style.css, and main.js' && echo "<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<meta name="viewport" content="width=device-width, initial-scale=1.0">
<title>Document</title>
<link rel="stylesheet" href="style.css">
<script defer src="main.js"></script>
</head>
<body>

</body>
</html>" > index.html && echo ':root{
 --clr-bg-light: #ffffff;
}

::-webkit-scrollbar{}

body{
 margin: 0;
 height: 100vh;
 width: 100vw;
 background-color: var(--clr-bg-light);
}' > style.css && touch main.js
